// reactive.ts - ì™„ì „í•œ êµ¬í˜„
type Context = Array<{ execute: () => void; dependencies: Set<any> }>;
const context: Context = [];

// ì–‘ë°©í–¥ êµ¬ë… ê´€ê³„ ì„¤ì •
function subscribe(running: { dependencies: Set<any> }, subscriptions: Set<any>) {
