// domains/shopping/state.ts
class ShoppingDomain {
  private state$ = observable({
    cart: {
      items: [] as CartItem[],
      coupon: null as string | null
    },

    checkout: {
      step: 'cart' as CheckoutStep,
      shippingAddress: null as Address | null,
      paymentMethod: null as PaymentMethod | null
    }
  });

  // Getters
