function createRealtimeDashboard() {
  const [data, setData] = createSignal([]);
  const [filters, setFilters] = createStore({
    status: "all",
    dateRange: { start: null, end: null },
    searchTerm: ""
  });

  // ë‹¤ë‹¨ê³„ íŒŒìƒ ìƒíƒœ
  const filtered = createMemo(() => {
    let result = data();

